import React from "react";

class ToDoItem extends React.Component {
  handleInputKeyUp(e) {
    if (e.keyCode === 13) {
      e.target.blur();
    }
  }
  render() {
    return (
      <div className="todo-item">
        {this.props.data.uuid} | {this.props.data.text}
      </div>
    );
  }
}
export default ToDoItem;
